var connection = new HubConnectionBuilder()
    .WithUrl("https://localhost:44388/Hubs/CardProductionHub", options =>
    {
        options.AccessTokenProvider = () =>
            Task.FromResult(yourJwtToken);
    }).WithAutomaticReconnect()
    .Build();

var Stockconnection = new HubConnectionBuilder()
    .WithUrl("https://localhost:44388/Hubs/StockMovementHub", options =>
    {
        options.AccessTokenProvider = () =>
            Task.FromResult(yourJwtToken);
    }).WithAutomaticReconnect()
    .Build();


await connection.StartAsync();
Console.WriteLine("Connected to SignalR Hub -- client -- cardProduction");

await Stockconnection.StartAsync();
Console.WriteLine("Connected to SignalR Hub -- client -- stock");



connection.On<string>("Connected", (message) =>
{
    Console.WriteLine($"[RECEIVED--Connected] {message}");
});
Stockconnection.On<string>("Connected", (message) =>
{
    Console.WriteLine($"[RECEIVED--Connected] {message}");
});


connection.On<object>("OnCardSentStatusChanged", (message) =>
